import React, { useState } from 'react';

const Challenges = () => {
  const challenges = [
    { id: 1, name: '30-Day No Sugar Challenge', description: 'Avoid sugar for 30 days to improve your health.', participants: 120 },
    { id: 2, name: 'Daily Meditation Challenge', description: 'Meditate for 10 minutes daily for 21 days.', participants: 85 },
    { id: 3, name: 'Morning Run Challenge', description: 'Run 5km every morning for 14 days.', participants: 45 }
  ];

  const [joinedChallenges, setJoinedChallenges] = useState([]);

  const handleJoinChallenge = (id) => {
    if (!joinedChallenges.includes(id)) {
      setJoinedChallenges([...joinedChallenges, id]);
    }
  };

  return (
    <div className="challenges">
      <h2>Available Challenges</h2>
      <div className="challenge-list">
        {challenges.map(challenge => (
          <div key={challenge.id} className="challenge-card card">
            <h3>{challenge.name}</h3>
            <p>{challenge.description}</p>
            <p><strong>Participants:</strong> {challenge.participants}</p>
            <button
              onClick={() => handleJoinChallenge(challenge.id)}
              disabled={joinedChallenges.includes(challenge.id)}
              className="join-challenge-button"
            >
              {joinedChallenges.includes(challenge.id) ? 'Joined ✓' : 'Join Challenge'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Challenges;
