import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Settings.css';

const Settings = () => {
  const [user, setUser] = useState(null);
  const [colorScheme, setColorScheme] = useState('light');
  const [backgroundTheme, setBackgroundTheme] = useState('teal');
  const [fontStyle, setFontStyle] = useState('Montserrat');
  const [pet, setPet] = useState('cat');
  const [petAnimation, setPetAnimation] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/');

    const fetchUser = async () => {
      try {
        const res = await axios.get('/api/auth/user', {
          headers: { 'x-auth-token': token },
        });
        setUser(res.data);
        setColorScheme(res.data.colorScheme);
        setBackgroundTheme(res.data.backgroundTheme);
        setFontStyle(res.data.fontStyle);
        setPet(res.data.pet);
        setPetAnimation(res.data.petAnimation);
        document.body.className = res.data.colorScheme;
        document.body.style.fontFamily = res.data.fontStyle;
        document.body.classList.remove('theme-teal', 'theme-purple', 'theme-blue');
        document.body.classList.add(`theme-${res.data.backgroundTheme}`);
      } catch (err) {
        localStorage.removeItem('token');
        navigate('/');
      }
    };

    fetchUser();
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      await axios.put(
        '/api/auth/settings',
        { colorScheme, backgroundTheme, fontStyle, pet, petAnimation },
        { headers: { 'x-auth-token': token } }
      );
      document.body.className = colorScheme;
      document.body.style.fontFamily = fontStyle;
      document.body.classList.remove('theme-teal', 'theme-purple', 'theme-blue');
      document.body.classList.add(`theme-${backgroundTheme}`);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.msg || 'Failed to update settings');
    }
  };

  return (
    <div className="settings">
      <h1>Settings</h1>
      <form onSubmit={handleSubmit}>
        <div className="settings-section">
          <h2>
            <i className="fas fa-paint-brush"></i> Appearance
          </h2>
          <div className="setting-item">
            <label>Color Scheme</label>
            <select
              value={colorScheme}
              onChange={(e) => setColorScheme(e.target.value)}
            >
              <option value="light">Light</option>
              <option value="dark">Dark</option>
            </select>
          </div>
          <div className="setting-item">
            <label>Background Theme</label>
            <select
              value={backgroundTheme}
              onChange={(e) => setBackgroundTheme(e.target.value)}
            >
              <option value="teal">Teal</option>
              <option value="purple">Purple</option>
              <option value="blue">Blue</option>
            </select>
          </div>
          <div className="setting-item">
            <label>Font Style</label>
            <select
              value={fontStyle}
              onChange={(e) => setFontStyle(e.target.value)}
            >
              <option value="Montserrat">Montserrat</option>
              <option value="Roboto">Roboto</option>
              <option value="Open Sans">Open Sans</option>
            </select>
          </div>
        </div>

        <div className="settings-section">
          <h2>
            <i className="fas fa-paw"></i> Pet Companion
          </h2>
          <div className="setting-item">
            <label>Pet Type</label>
            <div className="pet-selector">
              <div
                className={`pet-option ${pet === 'cat' ? 'selected' : ''}`}
                onClick={() => setPet('cat')}
              >
                <img src="/assets/cat.png" alt="Cat" className="pet-icon" />
                <span className={colorScheme === 'light' ? 'color-light' : 'color-dark'}>Cat</span>
              </div>
              <div
                className={`pet-option ${pet === 'dog' ? 'selected' : ''}`}
                onClick={() => setPet('dog')}
              >
                <img src="/assets/dog.png" alt="Dog" className="pet-icon" />
                <span className={colorScheme === 'light' ? 'color-light' : 'color-dark'}>Dog</span>
              </div>
            </div>
          </div>
          <div className="setting-item">
            <label>Pet Animation</label>
            <select
              value={petAnimation ? 'true' : 'false'}
              onChange={(e) => setPetAnimation(e.target.value === 'true')}
            >
              <option value="true">Enabled</option>
              <option value="false">Disabled</option>
            </select>
          </div>
        </div>

        <div className="settings-buttons">
          <button type="submit" className="save-settings-button">
            <i className="fas fa-save"></i> Save Settings
          </button>
          <button
            type="button"
            className="back-home-button"
            onClick={() => navigate('/dashboard')}
          >
            <i className="fas fa-arrow-left"></i> Back to Dashboard
          </button>
        </div>
      </form>
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default Settings;
