import React from 'react';

const Pet = ({ user, goals }) => {
  const completedGoals = goals.filter(g => g.completed).length;
  const messages = [
    `Great job! You've completed ${completedGoals} goals!`,
    "Keep it up! You're doing awesome!",
    "One more goal to go—let's do this!",
    "I'm proud of you! Keep the streak alive!"
  ];

  return (
    <div className="pet">
      <h3>Your Pet Companion</h3>
      <p>{user?.petType || 'Dog'} says: "{messages[Math.floor(Math.random() * messages.length)]}"</p>
    </div>
  );
};

export default Pet;
