import React from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Statistics = ({ goals, totalCompleted, totalGoals }) => {
  const data = {
    labels: ['Daily', 'Weekly', 'Monthly', 'Yearly'],
    datasets: [{
      label: 'Completed Goals',
      data: [
        goals.filter(g => g.type === 'daily' && g.completed).length,
        goals.filter(g => g.type === 'weekly' && g.completed).length,
        goals.filter(g => g.type === 'monthly' && g.completed).length,
        goals.filter(g => g.type === 'yearly' && g.completed).length
      ],
      backgroundColor: [
        'linear-gradient(135deg, #77D7F7 0%, #4DA8DA 100%)',
        'linear-gradient(135deg, #81C784 0%, #4CAF50 100%)',
        'linear-gradient(135deg, #FFCA28 0%, #FFB300 100%)',
        'linear-gradient(135deg, #F48FB1 0%, #EC407A 100%)'
      ].map(gradient => {
        const ctx = document.createElement('canvas').getContext('2d');
        const grad = ctx.createLinearGradient(0, 0, 0, 400);
        const [start, end] = gradient.match(/#[0-9a-fA-F]{6}/g);
        grad.addColorStop(0, start);
        grad.addColorStop(1, end);
        return grad;
      }),
      borderColor: [
        '#4DA8DA',
        '#4CAF50',
        '#FFB300',
        '#EC407A'
      ],
      borderWidth: 2,
      borderRadius: 5,
      hoverBackgroundColor: [
        'rgba(77, 215, 247, 0.9)',
        'rgba(129, 199, 132, 0.9)',
        'rgba(255, 202, 40, 0.9)',
        'rgba(244, 143, 177, 0.9)'
      ],
      hoverBorderColor: [
        '#2E86C1',
        '#388E3C',
        '#FFA000',
        '#D81B60'
      ],
      hoverBorderWidth: 3
    }]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 14,
            family: 'Montserrat',
            weight: '500'
          },
          color: '#00796b'
        }
      },
      title: {
        display: true,
        text: 'Completed Goals by Type',
        font: {
          size: 18,
          family: 'Montserrat',
          weight: '600'
        },
        color: '#00796b'
      },
      tooltip: {
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        titleFont: { family: 'Montserrat', size: 14 },
        bodyFont: { family: 'Montserrat', size: 12 },
        cornerRadius: 8
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Number of Goals',
          font: {
            size: 14,
            family: 'Montserrat'
          },
          color: '#00796b'
        },
        grid: {
          color: 'rgba(0, 0, 0, 0.1)'
        },
        ticks: {
          font: {
            family: 'Montserrat'
          },
          color: '#333'
        }
      },
      x: {
        title: {
          display: true,
          text: 'Goal Type',
          font: {
            size: 14,
            family: 'Montserrat'
          },
          color: '#00796b'
        },
        grid: {
          display: false
        },
        ticks: {
          font: {
            family: 'Montserrat'
          },
          color: '#333'
        }
      }
    },
    animation: {
      duration: 1500,
      easing: 'easeOutQuart'
    }
  };

  const successRate = totalGoals > 0 ? (totalCompleted / totalGoals) * 100 : 0;

  return (
    <div className="statistics card">
      <h2>Statistics</h2>
      <div className="success-rate">
        <p><strong>Success Rate:</strong> {successRate.toFixed(1)}%</p>
        <div className="progress-bar">
          <div className="progress-fill" style={{ width: `${successRate}%` }}></div>
        </div>
      </div>
      <Bar data={data} options={options} />
    </div>
  );
};

export default Statistics;
