import React from 'react';
import axios from 'axios';

const GoalList = ({ goals, setGoals, onGoalCompleted }) => {
  const handleComplete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.put(
        `/api/goals/${id}`,
        { completed: true },
        { headers: { 'x-auth-token': token } }
      );
      setGoals((prevGoals) =>
        prevGoals.map((goal) => (goal._id === id ? res.data : goal))
      );
      onGoalCompleted();
    } catch (err) {
      console.error('Failed to complete goal:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/goals/${id}`, {
        headers: { 'x-auth-token': token },
      });
      setGoals((prevGoals) => prevGoals.filter((goal) => goal._id !== id));
    } catch (err) {
      console.error('Failed to delete goal:', err);
    }
  };

  return (
    <div className="goal-list card">
      <h2>Your Goals</h2>
      {goals.length === 0 ? (
        <p>No goals yet. Add one above!</p>
      ) : (
        <ul>
          {goals.map((goal) => (
            <li key={goal._id} className="goal-item">
              <div className="goal-info">
                <span>{goal.name}</span>
                <span className="frequency">{goal.frequency}</span>
                {goal.completed && (
                  <span className="completed-marker">âœ” Completed</span>
                )}
                {goal.streak > 0 && (
                  <span className="streak">ðŸ”¥ Streak: {goal.streak}</span>
                )}
              </div>
              <div className="goal-actions">
                {!goal.completed && (
                  <button
                    onClick={() => handleComplete(goal._id)}
                    className="complete-button"
                  >
                    Complete
                  </button>
                )}
                <button
                  onClick={() => handleDelete(goal._id)}
                  className="delete-button"
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default GoalList;
