import React, { useState } from 'react';
import axios from 'axios';

const GoalForm = ({ setGoals, onGoalAdded }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [type, setType] = useState('daily');
  const [priority, setPriority] = useState('medium');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const newGoal = { name, description, type, priority };
    try {
      const res = await axios.post('/api/goals', newGoal, { headers: { 'x-auth-token': token } });
      setGoals(prev => [...prev, res.data]);
      setName(''); setDescription(''); setType('daily'); setPriority('medium');
      if (onGoalAdded) onGoalAdded(); // Notify parent of goal addition
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="goal-form card">
      <input type="text" placeholder="Goal Name" value={name} onChange={(e) => setName(e.target.value)} required />
      <textarea placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
      <select value={type} onChange={(e) => setType(e.target.value)}>
        <option value="daily">Daily</option>
        <option value="weekly">Weekly</option>
        <option value="monthly">Monthly</option>
        <option value="yearly">Yearly</option>
      </select>
      <select value={priority} onChange={(e) => setPriority(e.target.value)}>
        <option value="low">Low</option>
        <option value="medium">Medium</option>
        <option value="high">High</option>
      </select>
      <button type="submit">Add Goal</button>
    </form>
  );
};

export default GoalForm;
