import React, { useEffect, useState } from 'react';
import axios from 'axios';
import GoalForm from './GoalForm';
import GoalList from './GoalList';
import Statistics from './Statistics';
import Pet from './Pet';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [goals, setGoals] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/');

    const fetchData = async () => {
      try {
        const [goalsRes, userRes] = await Promise.all([
          axios.get('/api/goals', { headers: { 'x-auth-token': token } }),
          axios.get('/api/auth/user', { headers: { 'x-auth-token': token } })
        ]);
        setGoals(goalsRes.data);
        setUser(userRes.data);
        document.body.className = userRes.data.colorScheme;
      } catch (err) {
        localStorage.removeItem('token');
        navigate('/');
      }
    };

    fetchData();
  }, [navigate]);

  const handleShare = (type) => {
    const completedCount = goals.filter(g => g.completed).length;
    const text = `I've completed ${completedCount} goals on HabitQuest! Rank: ${user.rank} #HabitQuest`;
    const url = type === 'feed' 
      ? `https://www.instagram.com/?text=${encodeURIComponent(text)}`
      : `https://www.instagram.com/stories?text=${encodeURIComponent(text)}`;
    window.open(url, '_blank');
  };

  return (
    <div className="dashboard">
      <h1>HabitQuest</h1>
      <button onClick={() => navigate('/settings')}>Settings</button>
      <button onClick={() => { localStorage.removeItem('token'); navigate('/'); }}>Logout</button>
      {user && (
        <div className="rank-container">
          <img src={`/assets/${user.rank}.png`} alt={user.rank} className="rank-image" />
          <span>Rank: {user.rank}</span>
        </div>
      )}
      <Pet user={user} goals={goals} />
      <GoalForm setGoals={setGoals} />
      <GoalList goals={goals} setGoals={setGoals} />
      <Statistics goals={goals} />
      <div className="social-share">
        <h3>Share Progress</h3>
        <button onClick={() => handleShare('feed')}>Instagram Feed</button>
        <button onClick={() => handleShare('story')}>Instagram Story</button>
      </div>
    </div>
  );
};

export default Dashboard;
