import React, { useState } from 'react';
import axios from 'axios';

const GoalForm = ({ setGoals, onGoalAdded }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [type, setType] = useState('daily');
  const [priority, setPriority] = useState('medium');
  const [icon, setIcon] = useState('default'); // New field for icon
  const [notes, setNotes] = useState(''); // New field for notes

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const newGoal = { name, description, type, priority, icon, notes };
    try {
      const res = await axios.post('/api/goals', newGoal, { headers: { 'x-auth-token': token } });
      setGoals(prev => [...prev, res.data]);
      setName(''); setDescription(''); setType('daily'); setPriority('medium'); setIcon('default'); setNotes('');
      if (onGoalAdded) onGoalAdded();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="goal-form card">
      <input type="text" placeholder="Goal Name" value={name} onChange={(e) => setName(e.target.value)} required />
      <textarea placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
      <select value={type} onChange={(e) => setType(e.target.value)}>
        <option value="daily">Daily</option>
        <option value="weekly">Weekly</option>
        <option value="monthly">Monthly</option>
        <option value="yearly">Yearly</option>
      </select>
      <select value={priority} onChange={(e) => setPriority(e.target.value)}>
        <option value="low">Low</option>
        <option value="medium">Medium</option>
        <option value="high">High</option>
      </select>
      <select value={icon} onChange={(e) => setIcon(e.target.value)}>
        <option value="default">Default Icon</option>
        <option value="star">‚≠ê Star</option>
        <option value="heart">‚ù§Ô∏è Heart</option>
        <option value="book">üìö Book</option>
        <option value="run">üèÉ Run</option>
        <option value="apple">üçé Apple</option>
      </select>
      <textarea placeholder="Notes (e.g., how did it go?)" value={notes} onChange={(e) => setNotes(e.target.value)} />
      <button type="submit">Add Goal</button>
    </form>
  );
};

export default GoalForm;
