import React, { useEffect, useState } from 'react';
import axios from 'axios';
import GoalForm from './GoalForm';
import GoalList from './GoalList';
import Statistics from './Statistics';
import Pet from './Pet';
import { useNavigate, Link } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const [goals, setGoals] = useState([]);
  const [user, setUser] = useState(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('overview');
  const [userAction, setUserAction] = useState(null); // Track user actions
  const [petMessage, setPetMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/');

    const fetchData = async () => {
      try {
        const [goalsRes, userRes] = await Promise.all([
          axios.get('/api/goals', { headers: { 'x-auth-token': token } }),
          axios.get('/api/auth/user', { headers: { 'x-auth-token': token } })
        ]);
        setGoals(goalsRes.data);
        setUser(userRes.data);
        document.body.className = userRes.data.colorScheme;
      } catch (err) {
        localStorage.removeItem('token');
        navigate('/');
      }
    };

    fetchData();
  }, [navigate]);

  // Pet messages
  const contextualMessages = {
    overview: "Welcome to the Overview! Check your progress here.",
    goals: "This is your Goals tab! Add or complete a goal to keep progressing.",
    statistics: "Here are your Statistics! See how you're doing over time.",
    achievements: "Your Achievements are here! Keep earning badges!",
    motivation: "Feeling inspired? Read today's motivational quote!",
    pet: "Hey, I'm your pet companion! Let's keep you motivated!",
    share: "Ready to share your progress? Post to Instagram!"
  };

  const guidanceMessages = {
    overview: "Review your stats to see where you stand!",
    goals: "Add a new goal to keep progressing, or mark one as complete!",
    statistics: "Analyze your stats to set new goals!",
    achievements: "Complete more goals to earn new badges!",
    motivation: "Let this quote inspire your next goal!",
    pet: "I'm here to cheer you on—check another tab!",
    share: "Share your progress to inspire others!"
  };

  const feedbackMessages = {
    addedGoal: "Great job adding a goal! Keep up the momentum!",
    completedGoal: "Awesome, you completed a goal! You're on fire!",
    noAction: "Hmm, looks like you haven't done anything yet—let's get started!",
    invalidGoal: "That goal name might be too short—try adding more details!"
  };

  // Switch pet messages every 5 seconds
  useEffect(() => {
    const updatePetMessage = () => {
      const messages = [
        contextualMessages[activeTab], // Contextual message based on tab
        guidanceMessages[activeTab],   // Guidance message
        userAction ? feedbackMessages[userAction] : feedbackMessages.noAction // Feedback based on action
      ];
      const randomMessage = messages[Math.floor(Math.random() * messages.length)];
      setPetMessage(randomMessage);
    };

    updatePetMessage(); // Initial message
    const interval = setInterval(updatePetMessage, 5000); // Update every 5 seconds

    return () => clearInterval(interval); // Cleanup on unmount
  }, [activeTab, userAction]);

  const handleShare = (type) => {
    const completedCount = goals.filter(g => g.completed).length;
    const text = `I've completed ${completedCount} goals on HabitQuest! Rank: ${user?.rank} #HabitQuest`;
    const url = type === 'feed' 
      ? `https://www.instagram.com/?text=${encodeURIComponent(text)}`
      : `https://www.instagram.com/stories?text=${encodeURIComponent(text)}`;
    window.open(url, '_blank');
    setUserAction('shared'); // Update user action for pet feedback
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  // For GoalForm to update user action
  const handleGoalAdded = () => {
    setUserAction('addedGoal');
  };

  const handleGoalCompleted = () => {
    setUserAction('completedGoal');
  };

  // Calculate progress to next rank
  const completedGoals = goals.filter(g => g.completed).length;
  const rankProgress = {
    bronze: { next: 'silver', goal: 5 },
    silver: { next: 'gold', goal: 10 },
    gold: { next: 'platinum', goal: 20 },
    platinum: { next: null, goal: null }
  };
  const currentRank = user?.rank || 'bronze';
  const nextRankInfo = rankProgress[currentRank];
  const progressPercent = nextRankInfo && nextRankInfo.goal ? (completedGoals / nextRankInfo.goal) * 100 : 100;

  // Achievements
  const achievements = [];
  if (completedGoals >= 1) achievements.push({ name: 'First Goal', icon: '/assets/achievement-first.png' });
  const maxStreak = Math.max(...goals.map(g => g.streak), 0);
  if (maxStreak >= 5) achievements.push({ name: '5-Day Streak', icon: '/assets/achievement-streak.png' });
  if (completedGoals >= 10) achievements.push({ name: '10 Goals', icon: '/assets/achievement-10goals.png' });

  // Motivational Quotes
  const quotes = [
    "Success is the sum of small efforts, repeated day in and day out. – Robert Collier",
    "The only way to do great work is to love what you do. – Steve Jobs",
    "You are never too old to set another goal or to dream a new dream. – C.S. Lewis",
    "It does not matter how slowly you go as long as you do not stop. – Confucius"
  ];
  const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];

  // Calculate quick stats
  const totalCompleted = completedGoals;
  const currentStreak = maxStreak;

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>HabitQuest</h1>
        <div className="user-profile">
          <div className="profile-icon" onClick={toggleDropdown}>
            {user ? user.username.charAt(0).toUpperCase() : 'U'}
          </div>
          {isDropdownOpen && (
            <div className="profile-dropdown">
              <div className="user-info">
                <p><strong>Username:</strong> {user?.username}</p>
                <p><strong>Email:</strong> {user?.email}</p>
                <p><strong>Rank:</strong> {user?.rank}</p>
              </div>
              <Link to="/settings" className="dropdown-item">
                <i className="fas fa-cog"></i> Settings
              </Link>
              <button onClick={handleLogout} className="dropdown-item logout">
                <i className="fas fa-sign-out-alt"></i> Logout
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Pet Companion (Visible on All Tabs) */}
      <div className="pet card">
        <Pet user={user} goals={goals} message={petMessage} />
      </div>

      {/* Tab Navigation */}
      <div className="tab-nav">
        <button
          className={`tab-button ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        <button
          className={`tab-button ${activeTab === 'goals' ? 'active' : ''}`}
          onClick={() => setActiveTab('goals')}
        >
          Goals
        </button>
        <button
          className={`tab-button ${activeTab === 'statistics' ? 'active' : ''}`}
          onClick={() => setActiveTab('statistics')}
        >
          Statistics
        </button>
        <button
          className={`tab-button ${activeTab === 'achievements' ? 'active' : ''}`}
          onClick={() => setActiveTab('achievements')}
        >
          Achievements
        </button>
        <button
          className={`tab-button ${activeTab === 'motivation' ? 'active' : ''}`}
          onClick={() => setActiveTab('motivation')}
        >
          Motivation
        </button>
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        {activeTab === 'overview' && (
          <div className="overview-tab">
            <div className="rank-section card">
              {user && (
                <div className="rank-container">
                  <img src={`/assets/${user.rank}.png`} alt={user.rank} className="rank-image animate-bounce" />
                  <div className="rank-details">
                    <span>Rank: {user.rank}</span>
                    {nextRankInfo.goal && (
                      <>
                        <p>{`${nextRankInfo.goal - completedGoals} goals to ${nextRankInfo.next}`}</p>
                        <div className="progress-bar">
                          <div className="progress-fill" style={{ width: `${Math.min(progressPercent, 100)}%` }}></div>
                        </div>
                      </>
                    )}
                  </div>
                </div>
              )}
            </div>

            <div className="quick-stats card">
              <h2>Quick Stats</h2>
              <p><strong>Completed Goals:</strong> {totalCompleted}</p>
              <p><strong>Current Streak:</strong> {currentStreak} days</p>
            </div>

            <div className="achievements-section card">
              <h2>Recent Achievements</h2>
              {achievements.length === 0 ? (
                <p>No achievements yet. Keep tracking goals to earn badges!</p>
              ) : (
                <div className="badge-list">
                  {achievements.slice(0, 3).map((achievement, index) => (
                    <div key={index} className="badge">
                      <img src={achievement.icon} alt={achievement.name} className="badge-image" />
                      <span>{achievement.name}</span>
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="quote-section card">
              <h2>Motivation for Today</h2>
              <p className="quote">"{randomQuote}"</p>
            </div>

            <div className="social-share-overview">
              <h3>Share Your Progress</h3>
              <button onClick={() => handleShare('feed')} className="share-button">
                <i className="fab fa-instagram"></i> Feed
              </button>
              <button onClick={() => handleShare('story')} className="share-button">
                <i className="fab fa-instagram"></i> Story
              </button>
            </div>
          </div>
        )}

        {activeTab === 'goals' && (
          <div className="goals-tab">
            <GoalForm setGoals={setGoals} onGoalAdded={handleGoalAdded} />
            <GoalList goals={goals} setGoals={setGoals} onGoalCompleted={handleGoalCompleted} />
          </div>
        )}

        {activeTab === 'statistics' && (
          <div className="statistics-tab">
            <Statistics goals={goals} />
          </div>
        )}

        {activeTab === 'achievements' && (
          <div className="achievements-tab">
            <div className="achievements-section card">
              <h2>Achievements</h2>
              {achievements.length === 0 ? (
                <p>No achievements yet. Keep tracking goals to earn badges!</p>
              ) : (
                <div className="badge-list">
                  {achievements.map((achievement, index) => (
                    <div key={index} className="badge">
                      <img src={achievement.icon} alt={achievement.name} className="badge-image" />
                      <span>{achievement.name}</span>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'motivation' && (
          <div className="motivation-tab">
            <div className="quote-section card">
              <h2>Motivation for Today</h2>
              <p className="quote">"{randomQuote}"</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
