import React, { useState } from 'react';

const CalendarView = ({ goals, onClose }) => {
  const today = new Date();
  const [year, setYear] = useState(today.getFullYear());
  const [month, setMonth] = useState(today.getMonth());

  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  const daysInMonth = (year, month) => new Date(year, month + 1, 0).getDate();
  const firstDayOfMonth = (year, month) => new Date(year, month, 1).getDay();

  // Map completed goals by date
  const completedByDate = {};
  goals.forEach(goal => {
    if (goal.completed) {
      const date = new Date(goal.createdAt);
      const dayKey = `${date.getFullYear()}-${date.getMonth()}-${date.getDate()}`;
      if (!completedByDate[dayKey]) completedByDate[dayKey] = [];
      completedByDate[dayKey].push(goal.name);
    }
  });

  const handlePrevMonth = () => {
    if (month === 0) {
      setMonth(11);
      setYear(year - 1);
    } else {
      setMonth(month - 1);
    }
  };

  const handleNextMonth = () => {
    if (month === 11) {
      setMonth(0);
      setYear(year + 1);
    } else {
      setMonth(month + 1);
    }
  };

  const days = [];
  const firstDay = firstDayOfMonth(year, month);
  const totalDays = daysInMonth(year, month);
  for (let i = 0; i < firstDay; i++) {
    days.push(null);
  }
  for (let i = 1; i <= totalDays; i++) {
    days.push(i);
  }

  return (
    <div className="calendar-popup-content">
      <div className="calendar-header">
        <button onClick={handlePrevMonth} className="calendar-nav-button">
          <i className="fas fa-chevron-left"></i>
        </button>
        <h3>{months[month]} {year}</h3>
        <button onClick={handleNextMonth} className="calendar-nav-button">
          <i className="fas fa-chevron-right"></i>
        </button>
        <button onClick={onClose} className="calendar-close-button">
          <i className="fas fa-times"></i>
        </button>
      </div>
      <div className="calendar-grid">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
          <div key={day} className="calendar-day-header">{day}</div>
        ))}
        {days.map((day, index) => {
          const date = day ? new Date(year, month, day) : null;
          const dayKey = date ? `${date.getFullYear()}-${date.getMonth()}-${date.getDate()}` : null;
          const completedGoals = dayKey && completedByDate[dayKey] ? completedByDate[dayKey] : [];
          const isToday = date && date.toDateString() === today.toDateString();
          return (
            <div key={index} className={`calendar-day ${day ? 'active' : ''} ${isToday ? 'today' : ''}`}>
              {day ? (
                <>
                  <span>{day}</span>
                  {completedGoals.length > 0 && (
                    <div className="completed-goals-tooltip">
                      <span className="completed-marker">✔️</span>
                      <div className="tooltip-content">
                        {completedGoals.map((goal, idx) => (
                          <span key={idx} className="goal-marker">{goal}</span>
                        ))}
                      </div>
                    </div>
                  )}
                </>
              ) : null}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default CalendarView;
